#include <iostream>#include <typeinfo>#include "../multi_array.h"int main(int p_argc, char* p_argv[]){	std::cout << "Base type of [int] is : " << typeid(multi_array<int>::base_type).name() << std::endl;	std::cout << "Base type of [int*] is : " << typeid(multi_array<int*>::base_type).name() << std::endl;	std::cout << "Base type of [int**] is : " << typeid(multi_array<int**>::base_type).name() << std::endl;	std::cout << "Base type of [double***] is : " << typeid(multi_array<double***>::base_type).name() << std::endl;	return 0;}