#include "tests.h"#include "../multi_array_range.h"using pps::multi_array::range;int main(){	int t1[20];	int* t1Begin = &t1[0];	int* t1End = &t1[20];	range<int> r1(t1Begin, t1End);	PPS_TESTS_ASSERT(r1.begin() == t1Begin);	PPS_TESTS_ASSERT(r1.end() == t1End);	range<int> r2(r1);	PPS_TESTS_ASSERT(r2.begin() == t1Begin);	PPS_TESTS_ASSERT(r2.end() == t1End);	range<int> r3(0, 0);	r3 = r2;	PPS_TESTS_ASSERT(r3.begin() == t1Begin);	PPS_TESTS_ASSERT(r3.end() == t1End);	return 0;}