#include <typeinfo>#include <vector>#include "tests.h"#include "../multi_array_traits_iterator.h"using pps::multi_array::traits_iterator;int main(){	PPS_TESTS_ASSERT(typeid(traits_iterator<int*>::iterator) == typeid(int*));	PPS_TESTS_ASSERT(typeid(traits_iterator<int*>::constIterator) == typeid(const int*));	PPS_TESTS_ASSERT(typeid(traits_iterator<double**>::iterator) == typeid(double**));	PPS_TESTS_ASSERT(typeid(traits_iterator<int***>::iterator) == typeid(int***));	PPS_TESTS_ASSERT(typeid(traits_iterator<std::vector<int>>::iterator) == typeid(std::vector<int>::iterator));	PPS_TESTS_ASSERT(typeid(traits_iterator<std::vector<int>>::constIterator) == typeid(std::vector<int>::const_iterator));	PPS_TESTS_ASSERT(typeid(traits_iterator<std::vector<std::vector<double>>>::iterator) == typeid(std::vector<std::vector<double>>::iterator));	PPS_TESTS_ASSERT(typeid(traits_iterator<std::vector<int>*>::iterator) == typeid(std::vector<int>::iterator*));	PPS_TESTS_ASSERT(typeid(traits_iterator<std::vector<int>**>::iterator) == typeid(std::vector<int>::iterator**));	return 0;}