#include "tests.h"#include "../multi_array_view.h"using pps::multi_array::view;using pps::multi_array::range;int main(){	view v1;	PPS_TESTS_ASSERT(v1.size() == 0);	v1[3][7];	PPS_TESTS_ASSERT(v1.size() == 2);	PPS_TESTS_ASSERT(v1.at(0) == range(3));	PPS_TESTS_ASSERT(v1.at(1) == range(7));	view v2(v1);	PPS_TESTS_ASSERT(v2.size() == v1.size());	PPS_TESTS_ASSERT(v2.at(0) == v1.at(0));	PPS_TESTS_ASSERT(v2.at(1) == v1.at(1));	PPS_TESTS_ASSERT(v1 == v2);	PPS_TESTS_ASSERT(!(v1 != v2));	view v3 = v2;	PPS_TESTS_ASSERT(v2 == v3);	return 0;}