#include "tests.h"#include "../include/pps/range/indexes.h"using pps::range::traits;using pps::range::indexes;int main(){	indexes i1 = indexes()[2][3];	traits<indexes>::iterator it1(begin(i1));	PPS_TESTS_ASSERT(*it1++ == 2);	PPS_TESTS_ASSERT(*it1++ == 3);	PPS_TESTS_ASSERT(it1 == end(i1));	indexes i2(++begin(i1), end(i1));	traits<indexes>::iterator it2(begin(i2));	PPS_TESTS_ASSERT(*it2++ == 3);	PPS_TESTS_ASSERT(it2 == end(i2));	return 0;}