#include <typeinfo>#include <vector>#include "tests.h"#include "../include/pps/range/traitsBaseType.h"using pps::range::traitsBaseType;int main(){	PPS_TESTS_ASSERT(typeid(traitsBaseType<float***, 3>::baseType) == typeid(float));	PPS_TESTS_ASSERT(typeid(traitsBaseType<float***, 2>::baseType) == typeid(float*));	PPS_TESTS_ASSERT(typeid(traitsBaseType<float***, 1>::baseType) == typeid(float**));	PPS_TESTS_ASSERT(typeid(traitsBaseType<char[20][10], 2>::baseType) == typeid(char));	PPS_TESTS_ASSERT(typeid(traitsBaseType<int***, 3>::baseType) == typeid(int));	PPS_TESTS_ASSERT(typeid(traitsBaseType<double[], 1>::baseType) == typeid(double));	PPS_TESTS_ASSERT(typeid(traitsBaseType<double[][10], 2>::baseType) == typeid(double));	PPS_TESTS_ASSERT(typeid(traitsBaseType<std::vector<double[][10]>, 3>::baseType) == typeid(double));	return 0;}