#include "tests.h"#include "../include/pps/range/viewSubRange.h"#include "../include/pps/range/viewSubRangeIterator.h"using pps::range::viewSubRange;using pps::range::viewSubRangeIterator;int main(){	viewSubRange v1(3);	viewSubRangeIterator it1(v1.begin());	PPS_TESTS_ASSERT(*it1++ == 0);	PPS_TESTS_ASSERT(*it1++ == 1);	PPS_TESTS_ASSERT(*it1++ == 2);	PPS_TESTS_ASSERT(*it1++ == 3);	PPS_TESTS_ASSERT(it1 == v1.end());	viewSubRange v2(1, 7, 3);	viewSubRangeIterator it2(v2.begin());	PPS_TESTS_ASSERT(*it2++ == 1);	PPS_TESTS_ASSERT(*it2++ == 4);	PPS_TESTS_ASSERT(*it2++ == 7);	PPS_TESTS_ASSERT(it2 == v2.end());	return 0;}